sys_script_include.script:
var CertOwnerUtil = Class.create();
CertOwnerUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getPossibleOwners: function() {


		var currentUserCompanyId = gs.getUser().getCompanyID();

		if(gs.getProperty("glide.sys.domain.partitioning") == 'true') {
			if (currentUserCompanyId != null) {
				var company = new GlideRecordSecure("core_company");
				if (company.get(currentUserCompanyId)) {
					var companyDomain = company.getValue("sys_domain");
					if (companyDomain != null) {
						domain = companyDomain;

						return 'sys_domain='+domain;
					}
				}
			}
			return 'sys_domain='+global.getDefaultDomain();
		}



		return '';
	},

    type: 'CertOwnerUtil'
});
